import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';
import { Trip } from '../models/trip.model';

@Injectable({
  providedIn: 'root',
})
export class TripsService {
  private trips: Trip[] = [
    {
      id: 1,
      title: 'Cairo Trip',
      description: 'Visit the Pyramids and Egyptian Museum',
      price: 15,
      imageUrl:
        'data:image/jpeg;base64,',
      duration: 3,
      location: 'Cairo',
      rating: 4.5,
    },
    {
      id: 2,
      title: 'Luxor Trip',
      description: 'Explore the Valley of Kings and Luxor Temple',
      price: 15,
      imageUrl:
        'data:image/jpeg;base64,',
      duration: 3,
      location: 'Luxor',
      rating: 4.8,
    },
    {
      id: 3,
      title: 'Alexandria Trip',
      description: 'Visit Qaitbay Citadel and Bibliotheca Alexandrina',
      price: 15,
      imageUrl:
        'data:image/jpeg;base64,',
      duration: 3,
      location: 'Alexandria',
      rating: 4.3,
    },
    {
      id: 4,
      title: 'Another Cairo Trip',
      description: 'Visit Khan el-Khalili bazaar',
      price: 15,
      imageUrl:
        'data:image/jpeg;base64,',
      duration: 3,
      location: 'Cairo',
      rating: 4.2,
    },
  ];

  constructor() {}

  getAllTrips(): Observable<Trip[]> {
    return of(this.trips);
  }

  getTrip(id: number): Observable<Trip | undefined> {
    const trip = this.trips.find((trip) => trip.id === id);
    return of(trip);
  }

  getFeaturedTrips(): Observable<Trip[]> {
    return of(this.trips.filter((trip) => trip.featured));
  }

  searchTrips(term: string): Observable<Trip[]> {
    if (!term.trim()) {
      return this.getAllTrips();
    }

    term = term.toLowerCase();
    const filteredTrips = this.trips.filter(
      (trip) =>
        trip.title.toLowerCase().includes(term) ||
        trip.description.toLowerCase().includes(term) ||
        trip.location.toLowerCase().includes(term) ||
        trip.category?.toLowerCase().includes(term)
    );

    return of(filteredTrips);
  }

  getTripsByCategory(category: string): Observable<Trip[]> {
    const filteredTrips = this.trips.filter(
      (trip) => trip.category?.toLowerCase() === category.toLowerCase()
    );

    return of(filteredTrips);
  }

  getMostCommonTrips(): Observable<Trip[]> {
    // For now, we'll return the top 3 trips by rating as "most common"
    return of(this.trips.sort((a, b) => b.rating - a.rating).slice(0, 3));
  }
}
